fn count_char(s: &str, target: char) -> usize {
    s.chars().filter(|&c| c == target).count()
}

fn find_basement_position() -> Option<usize> {
    let instructions = INPUT;
    let mut floor = 0;

    for (i, c) in instructions.chars().enumerate() {
        match c {
            '(' => floor += 1,
            ')' => floor -= 1,
            _ => continue,
        }

        if floor == -1 {
            return Some(i + 1); // Return 1-based position
        }
    }

    None // Return None if basement is never reached
}

pub fn solve_day_one_part_one() {
    let instructions = INPUT;

    let count_up = count_char(instructions, '(');
    let count_down = count_char(instructions, ')');

    let result = if count_up > count_down {
        count_up - count_down
    } else {
        count_down - count_up
    };

    println!("Santa ends at floor: {}", result);
}

pub fn solve_day_two_part_two() {
    match find_basement_position() {
        Some(position) => println!("Santa enters the basement at position: {}", position),
        None => println!("Santa never enters the basement."),
    }
}
